import * as yup from "yup";
export const LoanSchema = yup.object().shape({
  name: yup.string().required("Name is required"),
  loanType: yup.string().oneOf(["Business Loan", "Afrifa Loans", "Abanaa Loans", "Olale Loans", "Car Loan", "Home Loan", "Buff Loans", "Government Loans", "Erasmus Loans", "Masadh Loans"], "Please select a valid option").required("Marriage status is required"),
