  annualSalary: yup.string().required("Annual Salary is required"),
  loanPurpose: yup.string().oneOf(["Health Care", "Big Purchase", "Investment", "Other"], "Please select a valid option").required("Loan Purpose is required"),
  idNumber: yup.string().min(6, "6 charaters or more").required("ID Number is required"),
  agreeBox: yup.boolean().oneOf([true], "Please tick checkbox")
