import React, { useEffect, useState } from "react";
import { userSchema } from "../validations/CustomerValidation";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import axios from "axios";

const fetchLoans = () => {
  return axios("http://localhost:8888/loans");
};

const ApplyLoan = () => {
  const [loans, setLoans] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  console.log(loans);

  useEffect(() => {
    fetchLoans()
      .then(({ data }) => {
        setLoans(data);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, []);

  const renderContent = () => {
    if (isLoading) {
      return (
        <div>
          <p>Please exercise some patience</p>
        </div>
      );
    }
    return loans.map((loan, index) => {
      return <option key={index}>{loan.name}</option>;
    });
  };

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userSchema),
  });

  const formSubmitHandler = (data) => {
    console.log(data);
    reset();
  };

  return (
    <div className="">
      <div className="flex flex-col justify-center items-center">
        <h1 className="mt-8 mb-2 font-extrabold text-4xl">Apply for a loan</h1>
        <h1 className="mt-2 font-light text-sm">Fill this form</h1>
        <div className="mt-12 w-full">
          <form onSubmit={handleSubmit(formSubmitHandler)}>
            <div className="flex flex-col max-w-lg mx-auto">
              <label className="mb-2">Full Name</label>
              <input
                {...register("name")}
                name="name"
                type="text"
                placeholder="Jane Doe"
                className="appearance-none border rounded-lg w-full py-4 px-4 text-gray-700 leading-tight focus:outline-none"
              />
              {errors.name ? (
                <span className="text-red-600">{errors.name.message}</span>
              ) : (
                ""
              )}
            </div>
            <div className="flex flex-col max-w-lg mx-auto">
              <label className="mb-2 mt-8">Select Loan</label>
              <select
                {...register("marriage")}
                name="marriage"
                defaultValue="Select an option"
                className="appearance-none border rounded-lg w-full py-4 px-4 text-gray-700 leading-tight focus:outline-none"
              >
                <option disabled hidden>
                  Select an option
                </option>
                {renderContent()}
              </select>
              {errors.companyName ? (
                <span className="text-red-600">
                  {errors.companyName.message}
                </span>
              ) : (
                ""
              )}
            </div>
            <div className="flex space-x-4 items-center mt-8 max-w-lg mx-auto justify-between">
              <div className="flex flex-col w-full ">
                <label className="mb-2 whitespace-nowrap">
                  Employment Status
                </label>
                <select
                  {...register("employment")}
                  name="employment"
                  defaultValue="Select an option"
                  className="appearance-none border rounded-lg w-full py-4 px-4 text-gray-700 leading-tight focus:outline-none"
                >
                  <option disabled hidden className="text-gray-50">
                    Select an option
                  </option>
                  <option>Employed</option>
                  <option>Unemployed</option>
                  <option>Self-Employed</option>
                </select>
                {errors.employment ? (
                  <span className="text-red-600">
                    {errors.employment.message}
                  </span>
                ) : (
                  ""
                )}
              </div>
              <div className="flex flex-col w-full ">
                <label className="mb-2 whitespace-nowrap">Annual Salary</label>
                <input
                  {...register("phone")}
                  name="phone"
                  type="number"
                  placeholder="0.00"
                  className="appearance-none border rounded-lg w-full py-4 px-4  text-gray-700 leading-tight focus:outline-none"
                />

                {errors.marriage ? (
                  <span className="text-red-600">
                    {errors.marriage.message}
                  </span>
                ) : (
                  ""
                )}
              </div>
            </div>

            <div className="flex flex-col max-w-lg mt-8 mx-auto">
              <label className="mb-2">Purpose of loan</label>
              <select
                {...register("employment")}
                name="employment"
                defaultValue="Select an option"
                className="appearance-none border rounded-lg w-full py-4 px-4 text-gray-700 leading-tight focus:outline-none"
              >
                <option disabled hidden className="text-gray-50">
                  Select an option
                </option>
                <option>Health Care</option>
                <option>Big Purchase</option>
                <option>Investment</option>
                <option>Other</option>
              </select>
              {errors.dob ? (
                <span className="text-red-600">{errors.dob.message}</span>
              ) : (
                ""
              )}
            </div>
            <div className="flex space-x-4 items-center max-w-lg mt-8 mx-auto justify-between">
              <div className="flex flex-col w-full ">
                <label className="mb-2 whitespace-nowrap">ID Number</label>
                <input
                  {...register("phone")}
                  name="phone"
                  type="number"
                  placeholder="0244123456"
                  className="appearance-none border rounded-lg w-full py-4 px-4  text-gray-700 leading-tight focus:outline-none"
                />
                {errors.phone ? (
                  <span className="text-red-600">{errors.phone.message}</span>
                ) : (
                  ""
                )}
              </div>
              {/* <div className="flex flex-col w-full ">
                <label className="mb-2 whitespace-nowrap">ID Card Type</label>
                <select
                  {...register("idType")}
                  name="idType"
                  defaultValue="Select an option"
                  className="appearance-none border rounded-lg w-full py-4 px-4  text-gray-700 leading-tight focus:outline-none"
                >
                  <option disabled hidden>
                    Select an option
                  </option>
                  <option>Passport</option>
                  <option>NHIS</option>
                  <option>Driver’s License</option>
                </select>
                {errors.idType ? (
                  <span className="text-red-600">{errors.idType.message}</span>
                ) : (
                  ""
                )}
              </div> */}
            </div>
            <div className="flex space-x-8 max-w-lg mx-auto border border-solid items-center">
              <div className="flex">
                <input type="checkbox" className="border border-solid" />
              </div>
              <label className="mb-2 mt-8 border border-solid">Address</label>

              {errors.address ? (
                <span className="text-red-600">{errors.address.message}</span>
              ) : (
                ""
              )}
            </div>
            <div className="flex flex-col max-w-lg mx-auto">
              <button className="border rounded-lg w-full py-4 px-4 mb-8 mt-12 bg-orange-600 leading-tight hover:bg-orange-500">
                <p className="text-gray-50">Submit</p>
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ApplyLoan;
