import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";

const fetchCustomer = () => {
  return axios("http://localhost:8888/customer/5");
};

const CustomerProfile = () => {
  const { ID } = useParams();
  const [customerID, setCustomerID] = useState(ID);
  const [customer, setCustomer] = useState([""]);
  const [isLoading, setIsLoading] = useState(true);

  console.log(customer);

  // useEffect(() => {
  //   setCustomerID(ID);
  //   const getDetails = async () => {
  //     try {
  //       const response = await axios.get(`http://localhost:8888/customer/5`);
  //       setCustomer(response?.data);
  //       setIsLoading(false);
  //       console.log(response);
  //       console.log(response?.data);
  //     } catch (err) {
  //       setIsLoading(true);
  //       // if (!err?.response) {
  //       //   showErrorToast("No Server Response");
  //       // } else if (err?.response.data.errorState === true) {
  //       //   showErrorToast(err.response.data.message);
  //       // }
  //     }
  //   };
  //   getDetails();
  // }, [ID, customerID]);

  useEffect(() => {
    fetchCustomer()
      .then(({ data }) => {
        setCustomer(data);
        // console.log(data);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, []);

  // console.log(fetchCustomer());
  // const renderContent = () => {
  //   if (isLoading) {
  //     return (
  //       <div>
  //         <p>Please exercise some patience</p>
  //       </div>
  //     );
  //   }
  //   return lists.map((list, index) => {
  //     return <CustomerList {...list} key={index} />;
  //   });
  // };

  return (
    <div>
      {customer[0].length === 0 ? <></> : customer[0].name}
      <div>
        <div className=" pt-8 ">
          <div className=" flex justify-between content-center mb-8">
            <p className="font-extrabold text-orange-600 text-2xl">
              Company bz
            </p>

            <div className="flex justify-between items-center space-x-4">
              <Link to="/existing-customers">
                <button className="border rounded-lg w-full  bg-gray-50  px-2 py-2 leading-tight hover:bg-gray-100">
                  <p className="font-bold text-sm text-gray-500">
                    Existing Customers
                  </p>
                </button>
              </Link>
              <Link to="/register">
                <button className="border rounded-lg w-full px-2 py-2 bg-orange-600 leading-tight hover:bg-orange-500">
                  <p className="font-bold text-sm text-gray-50">Register</p>
                </button>
              </Link>
            </div>
          </div>
        </div>
        <div className="flex flex-col">
          <div className="mx-auto">
            <p className="font-bold text-md mb-4">Available Loans</p>
          </div>
        </div>
        <div>
          <Link to="/apply"></Link>
        </div>
      </div>
    </div>
  );
};

export default CustomerProfile;
