import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CustomerList from "../components/CustomerList";
import axios from "axios";

const fetchCustomers = () => {
  return axios("http://localhost:8888/customers");
};

const Customers = () => {
  const [lists, setList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchCustomers()
      .then(({ data }) => {
        setList(data);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, []);

  const renderContent = () => {
    if (isLoading) {
      return (
        <div>
          <p>Please exercise some patience</p>
        </div>
      );
    }
    return lists.map((list, index) => {
      return <CustomerList {...list} key={index} />;
    });
  };

  return (
    <div>
      <div className=" pt-8 ">
        <div className=" flex justify-between content-center mb-8">
          <p className="font-extrabold text-orange-600 text-2xl">Company bz</p>
        </div>
      </div>
      <div className="flex flex-col">
        <div className="mx-auto">
          <p className="font-bold text-md mb-4">Customers</p>
        </div>
      </div>
      <div>
        <Link to={`/customer-profile/${""}`}>{renderContent()}</Link>
      </div>
    </div>
  );
};

export default Customers;
