import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";
import pfp from "../assets/pfp.svg";

const fetchCustomer = () => {
  return axios("http://localhost:8888/customer/5");
};

const CustomerProfile = () => {
  const { ID } = useParams();
  const [customerID, setCustomerID] = useState(ID);
  const [customer, setCustomer] = useState([""]);
  const [isLoading, setIsLoading] = useState(true);

  console.log(customer);

  // useEffect(() => {
  //   setCustomerID(ID);
  //   const getDetails = async () => {
  //     try {
  //       const response = await axios.get(`http://localhost:8888/customer/5`);
  //       setCustomer(response?.data);
  //       setIsLoading(false);
  //       console.log(response);
  //       console.log(response?.data);
  //     } catch (err) {
  //       setIsLoading(true);
  //       // if (!err?.response) {
  //       //   showErrorToast("No Server Response");
  //       // } else if (err?.response.data.errorState === true) {
  //       //   showErrorToast(err.response.data.message);
  //       // }
  //     }
  //   };
  //   getDetails();
  // }, [ID, customerID]);

  useEffect(() => {
    fetchCustomer()
      .then(({ data }) => {
        setCustomer(data);
        // console.log(data);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  }, []);

  // console.log(fetchCustomer());
  // const renderContent = () => {
  //   if (isLoading) {
  //     return (
  //       <div>
  //         <p>Please exercise some patience</p>
  //       </div>
  //     );
  //   }
  //   return lists.map((list, index) => {
  //     return <CustomerList {...list} key={index} />;
  //   });
  // };

  return (
    <div>
      <div className=" pt-8 ">
        <div className=" flex justify-between content-center mb-8">
          <p className="font-extrabold text-orange-600 text-2xl">
            User Profile
          </p>
        </div>
      </div>
      <div className="border border-solid p-4 rounded-lg flex">
        <div className="flex items-center">
          <img src={pfp} alt="" />
        </div>
        <div className="flex flex-col ml-8  w-full">
          <div className="flex mx-4 my-6 items-center border border-solid">
            <p className="font-bold text-xl">Name:</p>
            <div className="flex w-full justify-end">
              <p className="font-light ml-[100px]">{customer[0].name}</p>
            </div>
          </div>
          <div className="flex mx-4 my-6 items-center border border-solid">
            <p className="font-bold text-xl whitespace-nowrap">
              Date of Birth:
            </p>
            <div className="flex w-full justify-end">
              <p className="font-light ml-[100px]">{customer[0].dob}</p>
            </div>
          </div>
          <div className="flex mx-4 my-6 items-center border border-solid">
            <p className="font-bold text-xl">Phone:</p>
            <div className="flex w-full justify-end">
              <p className="font-light ml-[100px]">{customer[0].phone}</p>
            </div>
          </div>
          <div className="flex mx-4 my-6 items-center border border-solid">
            <p className="font-bold text-xl">Address:</p>
            <div className="flex w-full justify-end">
              <p className="font-light ml-[100px]">{customer[0].address}</p>
            </div>
          </div>
        </div>
      </div>
      <div>a</div>
    </div>
  );
};

export default CustomerProfile;
